{
    "$schema": "https://docs.renovatebot.com/renovate-schema.json",
    "extends": [
        "config:base",
        ":semanticCommits",
        ":dependencyDashboard",
        ":automergeDigest",
        ":automergeBranch",
    ],
    "platform": "gitea",
    "autodiscover": false,
    "dependencyDashboard": true,
    "dependencyDashboardTitle": "Renovate Dashboard 🤖",
    "dryRun": false,
    "printConfig": true,
    "kubernetes": {
        "fileMatch": [
            ".yaml$"
        ]
    },
    "major": {
        "dependencyDashboardApproval": true
    },
    repositories: ["gitea_admin/home-ops"]
}

proxy_set_header Accept-Encoding "";
sub_filter '</head>' '<link rel="stylesheet" type="text/css" href="https://theme-park.${SECRET_DOMAIN}/css/base/mylar/plex.css"></head>';
sub_filter_once on;
