{
    "$schema": "https://docs.renovatebot.com/renovate-schema.json",
    "extends": [
        "config:base",
        ":semanticCommits",
        ":dependencyDashboard",
        ":automergeDigest",
        ":automergeBranch",
    ],
    "platform": "gitea",
    "autodiscover": false,
    "dependencyDashboard": true,
    "dependencyDashboardTitle": "Renovate Dashboard 🤖",
    "dryRun": false,
    "printConfig": true,
    "kubernetes": {
        "fileMatch": [
            ".yaml$"
        ]
    },
    "major": {
        "dependencyDashboardApproval": true
    },
    repositories: ["gitea_admin/home-ops"]
}

server {
  listen 8080;
  server_name www.bandcamp.tv bandcamp.tv;
  return 301 $scheme://albatrossflavour.com;
}

server {
  listen 8080;
  server_name www.toodlepip.com.au toodlepip.com.au;
  return 301 $scheme://www.etsy.com/au/shop/ToodlepipdesignsAU;
}

server {
  listen 8080;
  server_name www.kalooma.org kalooma.org;
  return 301 $scheme://youtube.com/pdlfeggs;
}

1:
server {
  server_name int.${DOMAIN};
  listen 8080;
  root /opt/bitnami/nginx/html/;
  index no_index;

  location / {
    autoindex on;
  }

  location ~ /\.git {
    deny all;
  }

  location ~ /README.md {
    deny all;
  }

  location ~ /LICENSE {
    deny all;
  }
}

0:
proxy_set_header        X-Real-IP                 $remote_addr;
proxy_set_header        X-Forwarded-For           $proxy_add_x_forwarded_for;
proxy_set_header        Cookie                    $http_cookie;
proxy_set_header        X-Forwarded-Proto         $scheme;
proxy_set_header        Upgrade                   $http_upgrade;
proxy_set_header        Connection                "upgrade";

proxy_set_header Accept-Encoding "";
sub_filter '</head>' '<link rel="stylesheet" type="text/css" href="https://theme-park.bjw-s.dev/css/base/readarr/space-gray.css"></head>';
sub_filter_once on;
