1:
server {
  server_name int.${DOMAIN};
  listen 8080;
  root /opt/bitnami/nginx/html/;
  index no_index;

  location / {
    autoindex on;
  }

  location ~ /\.git {
    deny all;
  }

  location ~ /README.md {
    deny all;
  }

  location ~ /LICENSE {
    deny all;
  }
}
