0:
.:1053 {
  errors
  log
  health {
    lameduck 5s
  }
  ready
  k8s_gateway ${SECRET_DOMAIN_INTERNAL} {
    apex k8s-gateway.networking
    resources Ingress Service
    ttl 300
  }
  prometheus 0.0.0.0:9153
  loop
  reload
  loadbalance
}

1:
proxy_set_header Accept-Encoding "";
sub_filter '</head>' '<link rel="stylesheet" type="text/css" href="https://themepark.${EXTERNAL_DOMAIN}/css/base/{{ appName }}/{{ theme }}.css"></head>';
sub_filter_once on;
