if [[ -f /data/package.json ]]; then
    npm install --save $(npm outdated | cut -d' ' -f 1 | sed '1d' | xargs -I '$' echo '$@latest' | xargs echo);
else
    echo "Skipping npm updates, no package.json found.";
fi

proxy_set_header Accept-Encoding "";
sub_filter '</head>' '<link rel="stylesheet" type="text/css" href="https://theme-park.chestr.dev/css/base/radarr/organizr.css"><link rel="stylesheet" type="text/css" href="https://theme-park.chestr.dev/css/addons/radarr/radarr-4k-logo/radarr-4k-logo.css"></head>';
sub_filter_once on;

