0:
proxy_set_header Accept-Encoding "";
sub_filter '</head>' '<link rel="stylesheet" type="text/css" href="https://theme-park.${EXTERNAL_DOMAIN}/css/base/sonarr/organizr.css"></head>';
sub_filter_once on;

- name: PROXY_ADDRESS_FORWARDING
  value: "true"
- name: KEYCLOAK_USER
  value: ${KEYCLOAK_USER}
- name: KEYCLOAK_PASSWORD
  value: ${KEYCLOAK_PASSWORD}

server {
  listen 8080;
  server_name static.${SECRET_DOMAIN};
  location / {
    proxy_set_header Host $http_host;
    proxy_pass https://s3.services.${SECRET_DOMAIN}:9000/static-content/;
  }
}
