server {
  listen       8080;
  server_name  router.sparks.codes;

  location / {
    proxy_set_header Host $host;
    proxy_set_header X-Real-IP $remote_addr;
    proxy_request_buffering off;
    proxy_ssl_verify off;

    # resolver 10.13.2.20;
    set $backend "https://10.10.0.1";
    proxy_pass $backend;
  }

  location ~ /\.ht {
    deny  all;
  }
}

server {
  listen        8080;
  server_name   pxe.sparks.codes;
  root          /app;

  location / {
    autoindex on;
  }
}

server {
  listen       8080;
  server_name  media.sparks.codes;

  return 302 http://media.sparks.codes:8096;
}

server {
  listen 8080;
  server_name www.bandcamp.tv bandcamp.tv;
  return 301 $scheme://albatrossflavour.com;
}

