server {
  server_name int.${DOMAIN};
  listen 8080;
  root /opt/bitnami/nginx/html/;
  index no_index;

  location / {
    autoindex on;
  }

  location ~ /\.git {
    deny all;
  }

  location ~ /README.md {
    deny all;
  }

  location ~ /LICENSE {
    deny all;
  }
}

proxy_set_header Accept-Encoding "";
sub_filter '</head>' '<link rel="stylesheet" type="text/css" href="https://themepark.${EXTERNAL_DOMAIN}/css/base/{{ appName }}/{{ theme }}.css"></head>';
sub_filter_once on;
